import PropTypes from "prop-types";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import Typography from "@mui/material/Typography";
import Box from "@mui/material/Box";
import React, { useContext, useEffect, useState } from "react";
import SessionContext from "../../components/session/SessionContext";
import { FormLabel, TextField } from "@material-ui/core";
import Card from "@mui/material/Card";
import CardHeader from "@mui/material/CardHeader";
import CardContent from "@mui/material/CardContent";
import Avatar from "@mui/material/Avatar";
import IconButton from "@mui/material/IconButton";
import { red } from "@mui/material/colors";
import MoreVertIcon from "@mui/icons-material/MoreVert";
import api from "../../components/API/API";
import { Button } from "@material-ui/core";
import Storage from "../../components/API/Storage";
import { Paper } from "@mui/material";
import { toast } from "react-toastify";
import { style } from "@mui/system";
import { countries } from "country-data";
import DeleteOutlineIcon from "@mui/icons-material/DeleteOutline";
import EditIcon from "@mui/icons-material/Edit";
import moment from "moment";
import CountrySelect from "../../components/country/countrylist";
import Grid from "@mui/material/Grid";
import OrderCard from "../../components/OrderCard/OrderCard";
function TabPanel(props) {
  const { children, item, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={item !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {item === index && (
        <Box sx={{ p: 3 }}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.number.isRequired,
  item: PropTypes.number.isRequired,
};

function a11yProps(index) {
  return {
    id: `simple-tab-${index}`,
    "aria-controls": `simple-tabpanel-${index}`,
  };
}

export default function BasicTabs() {
  useEffect(() => {
    //get my post
    const getpost = async () => {
      const { data } = await api.get(`/userpost/${id}`);
      setmypost(data);
    };
    //get my sent orders
    const getsentorder = async () => {
      const { data } = await api.get(`/myorder/${id}`);
      setmyorder(data);
    };
    //get my sent orders
    const getreceivdorder = async () => {
      const { data } = await api.get(`/postorder/${id}`);
      setReceivd(data);
    };
    const getmyinfo = async () => {
      const { data } = await api.get(`/user`);
      setMyinfo(data);
    };

    getpost();
    getsentorder();
    getreceivdorder();
    getmyinfo();
  }, []);
  const altimage = Storage + "avatar.png";

  const [item, setValue] = React.useState(0);
  const {
    session: { user },
    actions: { logout },
  } = useContext(SessionContext);
  const deleteOrder = async (orderid) => {
    try {
      await api.delete(`deleteOrder/${orderid}`);
      let filter = [...order].filter((order) => order.order_id !== orderid);
      setmyorder(filter);
    } catch (error) {
      return toast.error("error");
    }
  };

  const { id, email, name, lastname, address, username, photo, phone } = user;
  const [mypost, setmypost] = useState([]);
  const [order, setmyorder] = useState([]);
  const [received, setReceivd] = useState([]);
  const [myinfo, setMyinfo] = useState([]);
  const [w, setwheight] = useState("");

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };
  const deletepost = async (id_deleted) => {
    try {
      api.delete(`/blogs/${id_deleted}`);

      let filter = [...mypost].filter((post) => post.id !== id_deleted);
      setmypost(filter);
    } catch (error) {
      toast.error(error);
    }
  };

  const [update_email, setEmail] = useState("");

  const hadleupdate = async (event) => {
    event.preventDefault();
    const fileInput = document.querySelector("#photo");

    const formData = new FormData();
    formData.append("name", "ok");
    formData.append("lastname", "reglastname");
    formData.append("username", "update_username");
    formData.append("phone", "55");

    formData.append("email", update_email);
    formData.append("status", "active");

    formData.append("photo", fileInput.files[0]);

    formData.append("address", "selectdcountry");
    console.log(formData);
    try {
      const { success } = await api.post(`/updateprofile/${id}`, formData, {
        headers: {
          Accept: "application/json",
          "Content-Type": "multipart/form-data",
        },
      });
      if (!success) {
        console.log("error");
      }
      toast.success("saved");
    } catch (error) {
      toast.error("Please Your Inputs");
    }
  };



  return (
    <Box sx={{ width: "100%" }}>
      <center>
        <br />
        <br />
        <br />
        <div
          style={{
            alignItems: "center",
            display: "flex",
            flexDirection: "column",
            marginTop: 10,
          }}
          class="profile-img"
        >
          <label htmlFor="photo">
            {photo ? (
              <Avatar
                alt={Storage + "avatar.png"}
                src={Storage + photo}
                aria-label="recipe"
              ></Avatar>
            ) : (
              <img
                style={{
                  maxWidth: 100,
                  maxHeight: 100,
                  borderRadius: "50%",
                  borderBlockColor: "red",
                }}
                src={altimage}
              />
            )}

            <EditIcon />
          </label>
          <br />
          <Button onClick={logout} variant="contained" color="Secondary">
            logout
          </Button>
          <div class="col-md-2">
            <br />
            <FormLabel style={{ color: "#007bff" }}>
              {name}&nbsp;{lastname}
            </FormLabel>
          </div>
        </div>
      </center>

      <Box sx={{ borderBottom: 1, borderColor: "divider" }}>
        <Tabs
          item={item}
          onChange={handleChange}
          aria-label="basic tabs example"
          centered
        >
          <Tab style={{ outlineWidth: 0 }} label="Posts" {...a11yProps(0)} />
          <Tab style={{ outlineWidth: 0 }} label="Info" {...a11yProps(1)} />
          <Tab style={{ outlineWidth: 0 }} label="Orders" {...a11yProps(2)} />
        </Tabs>
      </Box>
      <TabPanel item={item} index={0}>
        <>
          <div className="parent-container">
            {mypost.map((post) => (
              <>
                <OrderCard
                  cost={post.cost}
                  v={post.id}
                  fullname={name + lastname}
                  change={(e) => console.log(e.target.value)}
                  post_date={post.post_date}
                  avatar={Storage + post.photo}
                  from_country={countries[post.from_country].name}
                  to_country={countries[post.to_country].name}
                  from_country_code={post.from_country}
                  to_country_code={post.to_country}
                  // note={(e) => setdescription(e.target.value)}
                  wheight={(e) => setwheight(e.target.value)}
                  // order={(postid) => neworder(post.id)}
                />
              </>
            ))}
          </div>
        </>
