import PropTypes from "prop-types";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import Typography from "@mui/material/Typography";
import Box from "@mui/material/Box";
import React, { useContext, useEffect, useState } from "react";
import SessionContext from "../../components/session/SessionContext";
import { FormLabel, TextField } from "@material-ui/core";
import Card from "@mui/material/Card";
import CardHeader from "@mui/material/CardHeader";
import CardContent from "@mui/material/CardContent";
import Avatar from "@mui/material/Avatar";
import IconButton from "@mui/material/IconButton";
import { red } from "@mui/material/colors";
import MoreVertIcon from "@mui/icons-material/MoreVert";
import api from "../../components/API/API";
import { Button } from "@material-ui/core";
import Storage from "../../components/API/Storage";
import { Paper } from "@mui/material";
import { toast } from "react-toastify";
import { style } from "@mui/system";
import { countries } from "country-data";
import DeleteOutlineIcon from "@mui/icons-material/DeleteOutline";
import EditIcon from "@mui/icons-material/Edit";
import moment from "moment";
import CountrySelect from "../../components/country/countrylist";
import Grid from '@mui/material/Grid';
import OrderCard from '../../components/OrderCard/OrderCard'




export default function BasicTabs() {
  const altimage = Storage + "avatar.png";

  const {
    session: { user },
    actions: { logout },
  } = useContext(SessionContext);
  const deleteOrder = async (orderid) => {
    try {
      await api.delete(`deleteOrder/${orderid}`);
      let filter = [...order].filter((order) => order.order_id !== orderid);
      setmyorder(filter);
    } catch (error) {
      return toast.error("error");
    }
  };

  const { id, email, name, lastname, address, username, photo, phone } = user;
  const [mypost, setmypost] = useState([]);
  const [order, setmyorder] = useState([]);
  const [received, setReceivd] = useState([]);
  const [myinfo,setMyinfo]=useState([]);
  const [w,setwheight]=useState()


  const deletepost = async (id_deleted) => {
    try {
      api.delete(`/blogs/${id_deleted}`);

      let filter = [...mypost].filter((post) => post.id !== id_deleted);
      setmypost(filter);
    } catch (error) {
      toast.error(error);
    }
  };

  const [update_email,setEmail]=useState("");


  const About = () => {
    return (
      <>
        <Paper
          elevation={3}


        >

          <form
            onSubmit={(e) => hadleupdate(e)}
            encType="multipart/form-data"
            action=""
          >

            <input
              type="file"
              id="photo"
              style={{
                visibility: "hidden",
                height: 0,
                width: 0,
                display: "none",
              }}
              accept="image/png, image/gif, image/jpeg"
            />
            <TextField
              id="outlined-basic"
              defaultValue={email}
              label="email"
              variant="outlined"
              size="small"
              onChange={(e=>setEmail(e.target.value))}

            />

             <TextField
              id="outlined-basic"
              defaultValue={username}
              label="@username"
              size="small"

              variant="outlined"
            />



           <TextField
              id="outlined-basic"
              defaultValue={phone}
              label="phone"
              variant="outlined"
              size="small"
            />

            <CountrySelect
              id="outlined-basic"
              label="address"
              variant="outlined"
              default={address}
            />




            <Button type="submit" variant="contained" color="primary">
              update profile
            </Button>
          </form>
        </Paper>
      </>
    );
  };

  const Myorder = () => {
    if (order === undefined || order.length == 0) {
      return <div>Make Some Order</div>;
    }
    return (
      <div>
        {order ? (
          <div className="parent-container">
            {order.map((myorder) => (
              <Paper
                style={{
                  marginLeft: 10,
                  display: "flex",
                  flexDirection: "column",
                  textAlign: "center",
                  paddingLeft: 20,
                  paddingRight: 20,
                }}
                square={true}
                elevation={3}
              >
                {myorder.order_id}
                <span># {myorder.post_id}</span>
                <TextField
                  align="center"
                  label="Order Status"
                  value={myorder.order_status}
                />
                <TextField
                  fullWidth
                  label="Order Date"
                  type="date"
                  value={myorder.date_order}
                />
                <TextField label="" value={myorder.space} />
                <TextField value={myorder.description} />
                <br />
                <Button variant="contained" color="primary">
                  Update Order
                </Button>
                &nbsp;
                <Button
                  onClick={(id) => deleteOrder(myorder.order_id)}
                  variant="contained"
                  color="Error"
                >
                  Cancel
                </Button>
                <br />
              </Paper>
            ))}
          </div>
        ) : (
          <div>no</div>
        )}
      </div>
    );
  };

  useEffect(() => {
    //get my post
    const getpost = async () => {
      const { data } = await api.get(`/userpost/${id}`);
      setmypost(data);
    };
    //get my sent orders
    const getsentorder = async () => {
      const { data } = await api.get(`/myorder/${id}`);
      setmyorder(data);
    };
    //get my sent orders
    const getreceivdorder = async () => {
      const { data } = await api.get(`/postorder/${id}`);
      setReceivd(data);
    };
    const getmyinfo = async () => {
      const { data } = await api.get(`/user`);
      setMyinfo(data)
    };

    getpost();
    getsentorder();
    getreceivdorder();
    getmyinfo();


  }, []);

  const Post = () => {
    if (mypost === undefined || mypost.length == 0) {
      return <div>No Post</div>;
    }
    return (
      <div>
        {mypost ? (
          <div className="parent-container">
            {mypost.map((post) => (

              <>
              <OrderCard
                cost={post.cost}
                v={post.id}
                fullname={name +lastname}
                change={(e) => console.log(e.target.value)}
                post_date={post.post_date}
                avatar={Storage + post.photo}
                from_country={countries[post.from_country].name}
                to_country={countries[post.to_country].name}
                from_country_code={post.from_country}
                to_country_code={post.to_country}
                // note={(e) => setdescription(e.target.value)}
                wheight={(e) => setwheight(e.target)}
                // order={(postid) => neworder(post.id)}
              />
              </>
            ))}
          </div>
        ) : (
          <div>no</div>
        )}
      </div>
    );
  };

  return (
      <center>
        <br />
        <br />
        <br />
        <div
          style={{
            alignItems: "center",
            display: "flex",
            flexDirection: "column",
            marginTop: 10,
          }}
          class="profile-img"
        >
          <label htmlFor="photo">
            {photo ? (
              <Avatar
                alt={Storage + "avatar.png"}
                src={Storage + photo}
                aria-label="recipe"
              ></Avatar>
            ) : (
              <img
                style={{
                  maxWidth: 100,
                  maxHeight: 100,
                  borderRadius: "50%",
                  borderBlockColor: "red",
                }}
                src={altimage}
              />
            )}

            <EditIcon />
          </label>
          <br />
          <Button onClick={logout} variant="contained" color="Secondary">
            logout
          </Button>
          <div class="col-md-2">
            <br />
            <FormLabel style={{ color: "#007bff" }}>
              {name}&nbsp;{lastname}
            </FormLabel>
          </div>
        </div>
        <Post />
      </center>


  );
}
