import React, { useEffect, useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import clsx from "clsx";
import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import CardMedia from "@material-ui/core/CardMedia";
import CardContent from "@material-ui/core/CardContent";
import CardActions from "@material-ui/core/CardActions";
import Collapse from "@material-ui/core/Collapse";
import Avatar from "@material-ui/core/Avatar";
import IconButton from "@material-ui/core/IconButton";
import Typography from "@material-ui/core/Typography";
import { red } from "@material-ui/core/colors";
import FavoriteIcon from "@material-ui/icons/Favorite";
import ShareIcon from "@material-ui/icons/Share";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import MoreVertIcon from "@material-ui/icons/MoreVert";
import SessionContext from "../../components/session/SessionContext";
import { useContext } from "react";
import { TextField } from "@mui/material";
import api from "../../components/API/API";
import CountrySelect from "../../components/country/countrylist";
import { Button } from "@material-ui/core";
import "./Home.css";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
const useStyles = makeStyles((theme) => ({
  parent: {
    marginTop: 20,
    marginLeft: 20,
    backgroundColor: "white",
    color: "#1976d2",
  },
  root: {
    maxWidth: 100,
    marginBottom: 20,
    height: 100,
    display: "flex",
  },
  media: {
    height: 0,
    paddingTop: "56.25%", // 16:9
  },
  expand: {
    transform: "rotate(0deg)",
    marginLeft: "auto",
    transition: theme.transitions.create("transform", {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: "rotate(180deg)",
  },
  avatar: {
    backgroundColor: red[500],
  },
}));

export default function RecipeReviewCard() {
  const [disablebtn, setdisablebutton] = useState(true);
  const [postdata, setData] = useState([]);
  const getPost = async () => {
    const { data } = await api.get("/blogs");
    setData(data);

    console.log(data);
  };
  async function neworder(postid) {
    const body = {
      user_re_id: id,
      post_id: postid,
      status: 1,
      date_order: "2021-09-10",
      description: "22",
    };
    // const { error, succes } =
   try {
    await api.post("/orders", body)
    console.log("success");
     let filter = [...postdata].filter((admin) => admin.post_id !== postid);
    setData(filter);

   } catch (error) {
    toast( "error","order not sended")
   }


  }

  const {
    session: { user },
  } = useContext(SessionContext);

  const { id, email, name, lastname, avatar } = user;
  const classes = useStyles();

  useEffect(async () => {
    await getPost();
  }, []);
  if (postdata.length > 0)
    return (
      <center>
        <br />
        <br />
        <br />

        <div className="parent-container">
          {postdata.map((post) => {
            return (
              <Card className={classes.parent} sx={{ maxWidth: 345 }}>
                <CardHeader
                  avatar={
                    <>
                      {" "}
                      <Avatar sx={{ bgcolor: red[500] }} aria-label="recipe">
                        R
                      </Avatar>
                      {post.name} {post.lastname}
                    </>
                  }
                  action={
                    <IconButton aria-label="settings">
                      <MoreVertIcon />
                    </IconButton>
                  }
                  title={post.title}
                  subheader={post.date_depart}
                />

                <CardContent>
                  <Typography variant="body2" color="text.secondary">
                    <tr>
                      <td >from: </td>
                      <td>{post.from_country} </td>
                      <td> {post.date_arrive}</td>
                    </tr>
                    <tr style={{color:"green"}}>
                      <td>to: </td>
                      <td>{post.to_country} </td>
                      <td>{post.date_depart}</td>
                    </tr>

                    <span> {post.note}</span>
                    <td style={{ display: "grid" }}>
                      <TextField
                        sx
                        id="outlined-number"
                        label="Number"
                        type="number"
                        InputLabelProps={{
                          shrink: true,
                        }}
                        onInput={(e) => {
                          e.target.value = Math.max(0, parseInt(e.target.value))
                            .toString()
                            .slice(0, 2);
                        }}
                      />

                      <br />
                      <Button
                        onClick={(id) => neworder(post.post_id)}
                        variant="contained"
                        color="primary"
                      >
                        order
                      </Button>
                    </td>
                  </Typography>
                </CardContent>
              </Card>
            );
          })}
        </div>
      </center>
    );
  else {
    return (
      <div>
        <br />
        <br />
        <br />
        <br />
        <br />
        <br /> <div>Loading</div>
      </div>
    );
  }
}
